mapper.select:
SELECT * FROM CAFE_BOARD
ORDER BY BDIDX DESC
LIMIT #{param1},#{param2}

SELECT * FROM CAFE_BOARD
WHERE BDCATEGORY LIKE #{param1}
LIMIT #{param2},#{param3}

SELECT * FROM CAFE_BOARD
WHERE BDTITLE LIKE #{param1}
LIMIT #{param2},#{param3}

mapper.update:
UPDATE CAFE_BOARD 
SET
BDTITLE =#{param2}, BDCOMMENT = #{param3}, BDIMG=#{param4},BDCATEGORY=#{param5}
WHERE
BDIDX = #{param1}

UPDATE CAFE_BOARD 
SET
BDTITLE =#{param2}, BDCOMMENT = #{param3}, BDCATEGORY=#{param4}
WHERE
BDIDX = #{param1}
